script:
if (router.args.length != 1) {
  view.set_page('invalid_request');
} else {
  view.set_page('collection_list');

  const db_name = router.args[0];
  const collection_list = storage.get_collection_list(db_name);
}

if (router.args.length != 2) {
  view.set_page('invalid_request');
} else {
  view.set_page('item_list');

  const db_name = router.args[0];
  const collection_name = router.args[1];
}

view.set_content_type('application/json');

if (router.args.length != 2) {
  view.echo(JSON.stringify({}));
  view.finish();
} else {
  const db_name = router.args[0];
  const collection_name = router.args[1];

  view.echo(storage.simple_find_all(db_name, collection_name, '{}'));
  view.finish();
}

if (router.args.length != 3) {
  view.set_page('invalid_request');
} else {
  view.set_page('edit_item');

  const db_name = router.args[0];
  const collection_name = router.args[1];
  const oid = router.args[2];

  const find_query = {
    '_id': {
      '$oid': oid
    }
  };

  const value = storage.simple_find_first(db_name, collection_name, JSON.stringify(find_query));
}

view.set_content_type('application/json');

if (router.args.length != 3) {
  view.echo(JSON.stringify({}));
  view.finish();
} else {
  const db_name = router.args[0];
  const collection_name = router.args[1];
  const oid = router.args[2];

  const find_query = {
    '_id': {
      '$oid': oid
    }
  };

  view.echo(storage.simple_find_first(db_name, collection_name, JSON.stringify(find_query)));
  view.finish();
}

view.set_content_type('application/json');

if (router.args.length != 3) {
  view.echo(JSON.stringify({
    'status': 'failed'
  }));
  view.finish();
} else {
  const db_name = router.args[0];
  const collection_name = router.args[1];
  const oid = router.args[2];
  const replacement_value = router.get_post();
  
  const query = {
    '_id': {
      '$oid': oid
    }
  };
  
  storage.simple_replace_first(db_name, collection_name, JSON.stringify(query), replacement_value);

  view.echo(JSON.stringify({
    'status': 'success'
  }));
  view.finish();
}

if (router.args.length != 2) {
  view.set_page('invalid_request');
} else {
  view.set_page('create_item');

  const db_name = router.args[0];
  const collection_name = router.args[1];
}

view.set_content_type('application/json');

if (router.args.length != 2) {
  view.echo(JSON.stringify({
    'status': 'failed'
  }));
} else {
  const db_name = router.args[0];
  const collection_name = router.args[1];
  const value = router.post;
  
  storage.simple_insert(db_name, collection_name, value);

  view.echo(JSON.stringify({
    'status': 'success'
  }));
  view.finish();
}

view.set_content_type('application/json');

if (router.args.length != 3) {
  view.echo(JSON.stringify({
    'status': 'failed'
  }));
} else {
  const db_name = router.args[0];
  const collection_name = router.args[1];
  const oid = router.args[2];

  storage.simple_delete_first(db_name, collection_name, '{"_id": {"$oid": "' + oid + '"}}');

  view.echo('{"status": "success"}');
  view.finish();
}
