script:
view.set_content_type('application/json');

var args = router.get_args();
var db_name = args[0];
var collection_name = args[1];
var oid = args[2];
var replacement = router.get_post();


storage.simple_replace_first(db_name, collection_name, '{"_id": {"$oid": "' + oid + '"}}', replacement);
view.echo('{"status": "success"}');
view.finish();

view.set_content_type('application/json');

var args = router.get_args();
var db_name = args[0];
var collection_name = args[1];
var value = router.get_post();


storage.simple_insert(db_name, collection_name, value);
view.echo('{"status": "success"}');
view.finish();

view.set_content_type('application/json');

var args = router.get_args();
var db_name = args[0];
var collection_name = args[1];
var oid = args[2];


storage.simple_delete_first(db_name, collection_name, '{"_id": {"$oid": "' + oid + '"}}');

view.echo('{"status": "success"}');
view.finish();
